import { useColorMode, Button ,Flex, Box, Center } from "@chakra-ui/react"
import { MoonIcon, SunIcon } from "@chakra-ui/icons"


export default function Toogle(){
  const { colorMode, toggleColorMode } = useColorMode()
  return (
    <>
      {/* <Button onClick={toggleColorMode} size="sm">
        Toggle {colorMode === 'light' ? 'Dark' : 'Light'}
      </Button> */}
      <Flex onClick={toggleColorMode}  bg={colorMode === "light"? "#e6e1e1": "#575555"} width="40px" borderRadius='12px' height="25px" align="center"p="1px" justify={colorMode === "light"? "flex-start":"flex-end"}>
        <Center bg={colorMode === "light"?"#575555":"#e6e1e1"} v  borderRadius="50%" w="20px" h="100%">
          {colorMode==="light"?<SunIcon color={"whiteAlpha.800"}/>:
            <MoonIcon  color={"blackAlpha.500"}/>}
        </Center> 
      </Flex>
    </>
      
  )
}
