import './App.css';
import { ChakraProvider, Flex, Input, Grid } from '@chakra-ui/react'
// import { ColorModeScript} from "@cha"
import NavBar from './components/navbar';
import { DataProvider } from "./context/DataContext";


function App() {
  return (
    <ChakraProvider>
      <DataProvider>
        <Flex w="100%" h="100svh"  justify="center" align="center">
          <Flex  maxW="480px" w="100%" maxH="896px" h="100%" direction="column" >
            <NavBar/>
            <Flex direction="column" justify="space-between" h="100%">
              <Flex h="25%" bg="red">

              </Flex>
              <Flex h="60%" bg="green">
                <Flex w="80%" bg="black">
                  <Grid templateColumns='repeat(5, 1fr)' gap="1rem">

                  </Grid>
                </Flex>
                <Flex w="20%" bg="red">

                </Flex>
              </Flex>
            </Flex>
          </Flex>
        </Flex>
      </DataProvider>
    </ChakraProvider>
  );
}

export default App;
